    private void cambioSignoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cambioSignoActionPerformed
        String numPantalla;
        if(pantalla.getText().length()!=0)
            if(Character.isDigit(pantalla.getText().charAt(pantalla.getText().length()-1)))
                numPantalla= pantalla.getText()+ "-";
            else{
                if(pantalla.getText().charAt(pantalla.getText().length()-1)=='+')
                    numPantalla= pantalla.getText().replace(pantalla.getText().charAt(pantalla.getText().length()-1), '-');
                else{
                     if(pantalla.getText().charAt(pantalla.getText().length()-1)=='-')
                        numPantalla= pantalla.getText().replace(pantalla.getText().charAt(pantalla.getText().length()-1), '+');
                    else
                        numPantalla= pantalla.getText()+ "";
                }
            }
        else
            numPantalla= pantalla.getText()+ "-";
            
            pantalla.setText(numPantalla);
    }//GEN-LAST:event_cambioSignoActionPerformed

