    private void puntoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_puntoActionPerformed
        String numPantalla;
        if(pantalla.getText().length()!=0)
            if(Character.isDigit(pantalla.getText().charAt(pantalla.getText().length()-1)))
                numPantalla= pantalla.getText()+ ".";
            else
                numPantalla= pantalla.getText()+ "";
            
        else
            numPantalla= pantalla.getText()+ "0.";
            
        pantalla.setText(numPantalla);
    }//GEN-LAST:event_puntoActionPerformed

