    private void parentesisDerActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_parentesisDerActionPerformed
        String numPantalla;
        if(pantalla.getText().length()!=0&&cont!=0){
            if(Character.isDigit(pantalla.getText().charAt(pantalla.getText().length()-1))){
                numPantalla= pantalla.getText()+ ")";
                cont--;
            }
                
            else
                numPantalla= pantalla.getText()+ "";
        }
            
            
        else
            numPantalla= pantalla.getText()+ "";
            
        pantalla.setText(numPantalla);
    }//GEN-LAST:event_parentesisDerActionPerformed

